import React from "react";
import styled from "styled-components";
export default function Welcome() {
  const WelcomeContainer = styled.div`
    width: 100%;
    height: 90vh;
    border: 1px solid red; /* BORDER TEST*/
  `;
  const Left = styled.div`
    width: 50%;
    height: 100%;
    border: 1px solid yellow; /* BORDER TEST*/

    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  `;
  const Center = styled.div`
    border: 1px solid yellow; /* BORDER TEST*/

    display: flex;
    justify-content: center;
    flex-direction: column;
  `;
  const Text = styled.h1`
    border: 1px solid yellow; /* BORDER TEST*/
    align-items: right;
    font-style: normal;
    font-weight: 900;
    font-size: 64px;
    line-height: 80px;
    color: white;
  `;
  const Button = styled.button`
    background: none;
    border: 5px solid #ffffff;
    box-sizing: border-box;
    border-radius: 40px;
    font-size: 24px;
    line-height: 30px;

    display: inline-block;
    color: #ffffff;
    text-align: center;
    font-size: 28px;
    width: 205px;
    height: 69px;
    margin: 20px 0;
  `;

  return (
    <WelcomeContainer>
      <Left>
        <Center>
          <Text>Sezon Sale </Text>
          <Text>20% off.</Text>
          <Text>For your Favorite </Text>
          <Text>Coffee</Text>
          <Button>Check out</Button>
        </Center>
      </Left>
    </WelcomeContainer>
  );
}
