import { colors } from "./colors";

export const theme = {
  colors,
  font: {
    thin: 300,
    regular: 400,
    bold: 800,
  },
  family: {
    main: " 'Livvic', sans-serif",
  },
  media: {
    phone: "@media (max-width: 800px)",
    desktop: "@media (max-width: 1024px)",
  },
};
